import {
  Input,
  Row,
  Divider,
  Select,
  Tooltip,
  Image,
  Avatar,
  Comment,
} from "antd";
import styles from "./ScreenListPost.module.css";
import avatar from "../../../Common/Image/avatar.png";
import img_login from "../../../Common/Image/img_login.png";
import { useDispatch, useSelector } from "react-redux";
import React, { createElement, useEffect, useState } from "react";
import { getPosst } from "../../../Redux/PostSlice";
import {
  DislikeFilled,
  DislikeOutlined,
  LikeFilled,
  LikeOutlined,
} from "@ant-design/icons";
const ScreenListPost = () => {
  const dispatch = useDispatch();
  const select = useSelector((data) => data.posts.value);
  const [likes, setLikes] = useState(0);
  const [dislikes, setDislikes] = useState(0);
  const [action, setAction] = useState(null);

  const like = () => {
    setLikes(1);
    setDislikes(0);
    setAction("liked");
  };

  const dislike = () => {
    setLikes(0);
    setDislikes(1);
    setAction("disliked");
  };

  const actions = [
    <Tooltip key="comment-basic-like" title="Like">
      <span onClick={like}>
        {createElement(action === "liked" ? LikeFilled : LikeOutlined)}
        <span className="comment-action">{likes}</span>
      </span>
    </Tooltip>,
    <Tooltip key="comment-basic-dislike" title="Dislike">
      <span onClick={dislike}>
        {React.createElement(
          action === "disliked" ? DislikeFilled : DislikeOutlined
        )}
        <span className="comment-action">{dislikes}</span>
      </span>
    </Tooltip>,
    <span key="comment-basic-reply-to">Reply to</span>,
  ];

  const falesback =
    "data:image/png;base64,";

  useEffect(() => {
    dispatch(getPosst());
  }, []);
  return select?.map((item) => (
    <div className={styles.all}>
      <div className={styles._container_top2}>
        <div>
          <div className={styles._avatar_1}>
            <div
              style={{
                width: 100,
                height: 100,
                borderRadius: 50,
                background: "#DCDFE8",
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              <img
                src={item?.userAvatar}
                alt=""
                style={{ width: 90, height: 90, borderRadius: 50 }}
              />
            </div>

            <h3 className={styles._title_avatr}>{item?.userName}</h3>
          </div>

          <div className={styles.body_title1}>{item?.content}</div>

          <div className={styles.body_title2}>Xem thÃªm</div>

          <div style={{ textAlign: "center" }}>
            <Image
              src={item?.imagePost == undefined ? "error" : item?.imagePost}
              alt=""
              style={{ width: 200, height: 200, padding: 20 }}
              fallback={falesback}
            />
          </div>
          <div style={{ padding: 20, boder: "1px solid #000" }}>
            <hr />
            <Comment
              actions={actions}
              author={<a>Han Solo</a>}
              avatar={
                <Avatar
                  src="https://joeschmoe.io/api/v1/random"
                  alt="Han Solo"
                />
              }
              content={
                <p>
                  We supply a series of design principles, practical patterns
                  and high quality design resources (Sketch and Axure), to help
                  people create their product prototypes beautifully and
                  efficiently.
                </p>
              }
              // datetime={
              //   <Tooltip title={moment().format("YYYY-MM-DD HH:mm:ss")}>
              //     <span>{moment().fromNow()}</span>
              //   </Tooltip>
              // }
            />
          </div>
        </div>
      </div>
    </div>
  ));
};
export default ScreenListPost;
